import React, { useEffect, useState } from 'react';
import Image from '../components/Image';
import { storage, db } from '../config/firebase';
import '../css/App.css';
import { getStorage, ref, list, getDownloadURL, getBlob } from "firebase/storage";
import { Link } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore"; 



function Start() {
  const [imageList, setImageList] = useState([]);
  console.log(imageList);

  useEffect(() =>{
    async function fetchData() {
      const newImages = [];
      const querySnapshot = await getDocs(collection(db, "js"));
      querySnapshot.forEach((doc) => {
        console.log(`${doc.id} => ${doc.data().description}`);
        newImages.push(doc.data());
      });
      setImageList(newImages);
    }
    fetchData();
  }, [])

  async function downloadImages() {
    // Loop through each image data object
    for (const imageData of imageList) {
      const { url, description } = imageData;
      try {
        // Fetch the image
        const response = await fetch(url);
        const blob = await response.blob();

        // Create a link element to download the image
        const link = document.createElement('a');
        link.href = URL.createObjectURL(blob);
        link.download = description || 'image'; // Use description as the filename if available, otherwise use 'image'

        // Programmatically click the link to trigger the download
        link.click();

        // Clean up
        URL.revokeObjectURL(link.href);
      } catch (error) {
        console.error('Error downloading image:', error);
      }
    }
  }


  return (
    <>
      <div className="image-galery">
        {
          imageList.map((element, index) => (
            <div key={index}>
            <img src={element.url}></img>
            <p>{element.description}</p>
            </div>
          ))
        }

      </div>
      <div className='button-container'>

        <Link to="/upload" className='button'>
          <div className='upload icon'></div>
        </Link>
        <Link to="/slideshow" className='button'><div className='fullscreen icon'></div></Link>
        <div className='button' onClick={downloadImages}><div className='download icon'></div></div>

      </div>

    </>
  );
}
export default Start;
